sqlMap.select:
/* sql id : cycleInputMonitorDao.selectOption */ SELECT DITC_CD AS "code", DITC_NM AS "name" FROM TB_COMM_CODE WHERE GRP_CD = #grpCd# AND USE_YN = 'Y'
VALUE_CD LIKE '%' || #valueCd# || '%'
ORDER BY V_ORDER

  		/* sql id : cycleInputMonitorDao.selectCycleInputMonitorList */
  		
		SELECT AA.* 
		FROM   (SELECT ROWNUM AS ROW_NUM, AA.*
				FROM   (  
		
			
			
			ORDER BY
			    D.DS_ID DESC
		
		
				) AA
			WHERE ROWNUM <= #end:NUMERIC#
			) AA
		WHERE AA.ROW_NUM >= #start:NUMERIC#

/* sql id : cycleInputMonitorDao.selectCycleInputMonitorChart */ SELECT TOT_CNT, AC_CNT, AW_CNT , NAW_CNT , pNAW_CNT , (CASE WHEN (TOT_CNT - pNAW_CNT) = 0 THEN 0 ELSE ROUND((AC_CNT + AW_CNT)*100 / (TOT_CNT - pNAW_CNT),2) END) AW_RATE FROM ( SELECT COUNT(*) AS TOT_CNT , SUM(CASE WHEN L.LDSTATE_CD = 'AC' THEN 1 ELSE 0 END) AS AC_CNT , SUM(CASE WHEN L.LDSTATE_CD IN ('AW') THEN 1 ELSE 0 END) AS AW_CNT
, SUM(CASE WHEN D.LOAD_CD NOT IN ('RECY06', 'RECY07') AND L.LDSTATE_CD NOT IN ('AC', 'AW') AND L.LOAD_PLAN_YMD < TO_CHAR(SYSDATE, 'YYYYMMDD') THEN 1 ELSE 0 END) AS NAW_CNT , SUM(CASE WHEN (D.LOAD_CD IN ('RECY06', 'RECY07') AND L.LDSTATE_CD NOT IN ('AC', 'AW') ) OR ( L.LDSTATE_CD NOT IN ('AC', 'AW') AND L.LOAD_PLAN_YMD >= TO_CHAR(SYSDATE, 'YYYYMMDD')) THEN 1 ELSE 0 END) AS PNAW_CNT
FROM TB_OPEN_DS D INNER JOIN TB_OPEN_LDLIST L ON D.DS_ID = L.DS_ID AND D.USE_YN = 'Y'
)
